public class Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 1500;
		int b = 2000;
		int c = 4000;
		
		if (a < b && a < c){
			System.out.println( "a is smallest number");
		}
		
		if (c > b || c > b){
			System.out.println( "c is not smallest number");
		}
		
	}

}
